/**
 * Sound utilities for the game
 * Using Base64 encoded audio to avoid having to load external files
 */

// Winner announcement sound (pre-generated MP3 that says "Winner!")
export const WINNER_SOUND_BASE64 = "data:audio/mp3;base64,";

// Dice roll sound (pre-generated MP3 of dice rolling sound)
export const DICE_ROLL_SOUND_BASE64 = "data:audio/mp3;base64,";

// Play a sound using the HTML5 Audio API
export function playSound(base64AudioData: string) {
  try {
    // Create a new audio element
    const audio = new Audio(base64AudioData);
    
    // Configure audio
    audio.volume = 0.5; // 50% volume
    
    // Play the sound
    const playPromise = audio.play();
    
    // Handle autoplay restrictions in modern browsers
    if (playPromise !== undefined) {
      playPromise.catch(error => {
        console.warn('Audio playback prevented by browser:', error);
      });
    }
    
    return true;
  } catch (error) {
    console.error('Error playing sound:', error);
    return false;
  }
}

// Play winner announcement
export function playWinnerSound() {
  return playSound(WINNER_SOUND_BASE64);
}

// Play dice roll sound
export function playDiceRollSound() {
  return playSound(DICE_ROLL_SOUND_BASE64);
}